                    $result['questions'][$k]['discussion']['log'][$log_index]['created_on'] = $logv['created_on'];
                    $log_index++;
                }
                $result['questions'][$k]['discussion']['log'][$log_index]['comments'] = $review_discussion[0]['remarks'];
